mapper.select:
		select 
		count(*) 
		from TB_BUS_PAXYD

            salesManId in(select teamMate from TB_TEAM where teamLeader =  #{teamLeader})
            and salesManId = #{teamMate}
            and name like "%"#{condition.name}"%"  
             and applicantTel like "%"#{condition.applicantTel}"%"  


            and checkState !=5 and  checkState !=6 
             and checkState =#{condition.customerState}  



            and checkState !=0 and checkState !=1 and checkState !=2 and checkState !=3 and checkState !=7 and checkState !=8 
            and checkState =#{condition.busSpeed}  


            and applyDate >= #{condition.applyDateStart}  
            and applyDate <= #{condition.applyDateEnd}   

        ORDER BY applyDate DESC

		select 
		*
		from TB_BUS_PAXYD  

            salesManId in(select teamMate from TB_TEAM where teamLeader =  #{teamLeader})
            and salesManId = #{teamMate}
            and name like "%"#{condition.name}"%"  
            and applicantTel like "%"#{condition.applicantTel}"%"  


            and checkState !=5 and checkState !=6 
             and checkState =#{condition.customerState}  



            and checkState !=0 and checkState !=1 and checkState !=2 and checkState !=3  and checkState !=7 and checkState !=8
            and checkState =#{condition.busSpeed}  


            and applyDate >= #{condition.applyDateStart}  
            and applyDate <= #{condition.applyDateEnd}   

        ORDER BY applyDate DESC
		limit #{queryInitCount}  , #{limit}
