mapper.select:
select name,
paxydBusId as id,
applicantTel,
applyDate,
type,
checkState
from TB_BUS_PAXYD
where salesManId = #{userId}

    and applicantTel = #{searchText}


    and checkState = #{state}


    and type = #{type}

mapper.update:
update TB_BUS_PAXYD
set salesManRemarkAndAdvice = #{suggestion}
where paxydBusId = #{paxydBusId}

update TB_USER
set uPhone = #{uPhone}
where userId = #{userId}
